import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";import{V as o,Y as r,aJ as a}from"./framework.980b93ee.js";const h='{"title":"Vue 3 + TypeScript + Vite","description":"","frontmatter":{},"headers":[{"level":2,"title":"Recommended IDE Setup","slug":"recommended-ide-setup"},{"level":2,"title":"Type Support For .vue Imports in TS","slug":"type-support-for-vue-imports-in-ts"}],"relativePath":"README.md","lastUpdated":1760688335683}',i={};function p(s,e,n,l,d,c){return o(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="vue-3-typescript-vite" tabindex="-1">Vue 3 + TypeScript + Vite <a class="header-anchor" href="#vue-3-typescript-vite" aria-hidden="true">#</a></h1><p>This template should help get you started developing with Vue 3 and TypeScript in Vite. The template uses Vue 3 <code>&lt;script setup&gt;</code> SFCs, check out the <a href="https://v3.vuejs.org/api/sfc-script-setup.html#sfc-script-setup" target="_blank" rel="noopener noreferrer">script setup docs</a> to learn more.</p><h2 id="recommended-ide-setup" tabindex="-1">Recommended IDE Setup <a class="header-anchor" href="#recommended-ide-setup" aria-hidden="true">#</a></h2><ul><li><a href="https://code.visualstudio.com/" target="_blank" rel="noopener noreferrer">VS Code</a> + <a href="https://marketplace.visualstudio.com/items?itemName=Vue.volar" target="_blank" rel="noopener noreferrer">Volar</a> (and disable Vetur) + <a href="https://marketplace.visualstudio.com/items?itemName=Vue.vscode-typescript-vue-plugin" target="_blank" rel="noopener noreferrer">TypeScript Vue Plugin (Volar)</a>.</li></ul><h2 id="type-support-for-vue-imports-in-ts" tabindex="-1">Type Support For <code>.vue</code> Imports in TS <a class="header-anchor" href="#type-support-for-vue-imports-in-ts" aria-hidden="true">#</a></h2><p>TypeScript cannot handle type information for <code>.vue</code> imports by default, so we replace the <code>tsc</code> CLI with <code>vue-tsc</code> for type checking. In editors, we need <a href="https://marketplace.visualstudio.com/items?itemName=Vue.vscode-typescript-vue-plugin" target="_blank" rel="noopener noreferrer">TypeScript Vue Plugin (Volar)</a> to make the TypeScript language service aware of <code>.vue</code> types.</p><p>If the standalone TypeScript plugin doesn&#39;t feel fast enough to you, Volar has also implemented a <a href="https://github.com/johnsoncodehk/volar/discussions/471#discussioncomment-1361669" target="_blank" rel="noopener noreferrer">Take Over Mode</a> that is more performant. You can enable it by the following steps:</p><ol><li>Disable the built-in TypeScript Extension <ol><li>Run <code>Extensions: Show Built-in Extensions</code> from VSCode&#39;s command palette</li><li>Find <code>TypeScript and JavaScript Language Features</code>, right click and select <code>Disable (Workspace)</code></li></ol></li><li>Reload the VSCode window by running <code>Developer: Reload Window</code> from the command palette.</li></ol>',8)])])}var f=t(i,[["render",p]]);export{h as __pageData,f as default};
